import React,{useState} from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import {Form, Input, Checkbox, Button} from 'antd';

const Signup =() =>{
    const [id, setId] = useState('');
    const [nick, setNick] = useState('');
    const [password, setPassword] = useState('');
    const [passwordChk, setPasswordChk] = useState('');
    const [term, setTerm] = useState(false);
    const [passwordError, setPasswordError] = useState(false);
    const [termError, setTermError] = useState(false);

    const onSubmit =(e)=>{
        e.preventDefault();
        if( password !== passwordChk){
            return setPasswordError(true);
        }
        if(!term){
            return setTermError(true);
        }

        console.log({id,nick,password,passwordChk,term}); //여기서 찾으면됨. 실제 배포때는 console.log지워주면됨. 웹팩때 한번에 지워주는거있음.
    }

    const onChangeId =(e)=>{
        setId(e.target.value);
    }

    const onChangeNick =(e)=>{
        setNick(e.target.value);
    }

    const onChangePassword =(e)=>{
        setPassword(e.target.value);
    }
    
    const onChangePasswordChk =(e)=>{
        setPasswordError(e.target.value !== password);
        setPasswordChk(e.target.value);
    }

    const onChangeTerm =(e)=>{
        setTermError(false);
        setTerm(e.target.checked); //약관동의 체크.
    }

    return (
        <>
        <Head>
        <title>노드버드</title>     
        <link rel ="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.css" />
        <script src ="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.js" />
        </Head>
        <AppLayout>
          <Form onSubmit={onSubmit} style={{padding : 10}}>
          <div>
              <label htmlFor="user-id">아이디</label>
              <br/>
              <Input name="user-id" value={id} required onChange={onChangeId} />
          </div>
          <div>
              <label htmlFor="user-nick">닉네임</label>
              <br/>
              <Input name="user-nick" value={nick} required onChange={onChangeNick} />
          </div>
          <div>
              <label htmlFor="user-pass">비밀번호</label>
              <br/>
              <Input name="user-pass" type="password" value={password} required onChange={onChangePassword} />
          </div>
          <div>
              <label htmlFor="user-pass-chk">비밀번호 체크</label>
              <br/>
              <Input name="user-pass-chk" type="password" value={passwordChk} required onChange={onChangePasswordChk} />
              {passwordError ? <div style = {{color : 'red'}}>비밀번호가 일치하지 않습니다.</div> : ''}
          </div>
          <div>
            <Checkbox name="user-term" value={term} onChange={onChangeTerm}>약관에 동의합니다.</Checkbox>
            {termError && <div style = {{color : 'red'}}>약관에 동의하셔야합니다</div>}
          </div>
          <div style={{marginTop : 10}}>
              <Button type="primary" htmlType ="submit">가입하기</Button>
          </div>
          </Form>
        </AppLayout>
        </>
    )
}

export default Signup;
