import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import {Menu, Input , Button,Row, Col, Avatar, Card } from 'antd';
//{children}은 부모컴포넌트에서 AppLayout 컴포넌트 밑에 jsx사용하는데 그 jsx들을 통째로 children이라고함.

const dummy ={
    nickname : "레오",
}

const AppLayout =({children}) =>{
    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                <Input.Search enterButton style={{verticalAlign : 'middle'}}/>                  
                </Menu.Item>
            </Menu>
           
            <Row> {/*자동으로 반응형이 됨. 반응형 xs는 모바일. 데탑 : sm(작은),md(중간),lg(큰).  */}
                <Col xs={24} md={6} >
                    <Card>
                       <Card.Meta   
                       avatar={<Avatar>{dummy.nickname}</Avatar>}   
                       title ={dummy.nickname}
                       />
                    </Card>
                    <Link href="/signup"><a><Button>회원가입</Button></a></Link>
                </Col>
                <Col xs={24} md={12}>
                  {children}
                </Col>
                <Col xs={24} md={6} >
                
                </Col>
            </Row>
           
        </div>
    )
}

AppLayout.propTypes = {
    children : PropTypes.node,
}

export default AppLayout;
